import pathlib
from shared.utils.constants import LOGLEVELS

def formatter(entry, **g):
	globals().update(g)
	if entry["__virtual"]:
		return entry["message"]
	lookup_table = {v: k for k, v in LOGLEVELS.items()}
	if lookup_table[entry["flag"]] in ("STDOUT", "STDERR"):
		return "%s --> %s" % (entry["timestamp"], entry["message"])
	file = pathlib.PurePath(entry["file"])
	return "%s [%s] %s [%s (QOS:%s)] %s at %s:%lu: %s" % (
		entry["timestamp"],
		lookup_table[entry["flag"]].rjust(6),
		entry["tag"]["processName"],
		"%s:%s" % (
			entry["threadID"],
			entry["tag"]["queueThreadLabel"]
		) if entry["threadID"] != entry["tag"]["queueThreadLabel"] else entry["threadID"],
		entry["tag"]["qosName"],
		entry["function"],
		"%s/%s" % (file.parent.name, file.name),
		entry["line"],
		entry["message"],
	)
